import React from 'react';

import {Image, StyleSheet, View} from 'react-native';

import Texto from '../../../componentes/texto';

export default function ItemDetail({detalhes}){

    return <><Texto style={style.nome}>{detalhes.nome}</Texto>
    <View style={style.fazenda}>
        <Image source={detalhes.logoFazenda} style={style.logo}></Image>
        <Texto style={style.nomeFazenda}>{detalhes.nomeFazenda}</Texto>
    </View>

    <Texto style={style.descricao}>{detalhes.descricao}</Texto>
    <Texto style={style.preco}>{detalhes.preco}</Texto></>

}

const style = StyleSheet.create({

    nome:{
        fontSize:26,
        lineHeight: 42,
        color: "#464646",
        fontWeight: "bold",

    },
    fazenda:{
        flexDirection:"row",
        paddingVertical:12
    },
    logo:{
        width: 32,
        height:32
    },
    nomeFazenda:{
        fontSize:16,
        lineHeight:26,
        marginLeft:12,
        fontFamily:"MontserratRegular"
    },
    descricao:{
        color:"#A3A3A3",
        fontSize:16,
        lineHeight:26
    },
    preco:{
        color:"#2A9F85",
        fontWeight:"bold",
        fontSize:26,
        lineHeight:42,
        margimTop:8
    },

})
